def makecontext(url,i):
    '''
    request = req.Request(url, headers = {"User-Agent":"Mozilla/5.0 (Macintosh; Intel Mac OS X 10_15_5) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/91.0.4472.77 Safari/537.36"})
    with req.urlopen(request) as response:
        data = response.read().decode("utf-8")
    context = bs4.BeautifulSoup(data,"html.parser")
    '''
    # request data is what has written in request payload
    requestData = makerequest(url,i)
    request = req.Request(url, headers = {
        "User-Agent":"Mozilla/5.0 (Macintosh; Intel Mac OS X 10_15_5) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/91.0.4472.77 Safari/537.36",
        "Content-Type":"Application/json"}, data = json.dumps(requestData).encode("utf-8"))
    # Here, data is a string then load to a dict
    with req.urlopen(request) as response:
        data = response.read().decode("utf-8")
    context = json.loads(data)
    return context

